import { useEffect, useState } from "react";
import FormInput from "../../../Custom/FormInput/FormInput";
import FileUpload from "../../../Custom/FileUpload/FileUpload";
import css from "./FormHeader.module.css";
import { useFormContext } from "react-hook-form";
import { domain } from "../../../../config";

const FormHeader = ({ img, setImg, updatePreview, imageError }) => {
  const { register } = useFormContext();
  const [preview, setPreview] = useState();
  const [errorText, setErrorText] = useState("");

  useEffect(() => {
    // create the preview
    if (!img) {
      const previewPath = updatePreview || "default.png";
      setPreview(`url("${domain}/static/RecipesImages/${previewPath}")`);
      return;
    }
    const objectUrl = URL.createObjectURL(img);
    setPreview(`url('${objectUrl}')`);

    // free memory when ever this component is unmounted
    return () => URL.revokeObjectURL(img);
  }, [img, updatePreview]);

  useEffect(() => {
    switch (imageError) {
      case "FORBIDDEN_TYPE":
        setErrorText("Неправильный тип файла");
        break;
      case "LIMIT_FILE_SIZE":
        setErrorText("Cлишком большой файл");
        break;
      case "UNDEFIEND_IMAGE_ERROR":
        setErrorText("Не удалось обновить аватар");
        break;
      default:
        setErrorText(null);
    }
  }, [imageError]);

  return (
    <div className={css.formHeader}>
      <div className={css.imgSection}>
        <div
          className={css.img}
          style={{
            backgroundImage: preview,
          }}
        ></div>
      </div>
      <div className={css.mainFields}>
        <FormInput name="name" label="Название" />
        <div className={css.typeContainer}>
          <select className={css.type} {...register("type")}>
            <option value="ХОЛОДНЫЙ НАПИТОК" defaultValue>
              Холодный напиток
            </option>
            <option value="ЧАЙ">Чай</option>
            <option value="ДЕСЕРТ">Десерт</option>
          </select>
        </div>

        <FileUpload img={img} setImg={setImg} className={css.fileUpload} />
        <div className={css.error}> {errorText} </div>
      </div>
    </div>
  );
};

export default FormHeader;
