import css from "./FileUpload.module.css";
import { ReactComponent as UploadSvg } from "../../../svg/Cloud-upload.svg";

const Input = ({ img, setImg, className, ...other }) => {
  return (
    <>
      <label htmlFor="file-upload" className={css.fileUpload + " " + className}>
        <UploadSvg />
        {!!img ? <p>Изменить</p> : <p>Загрузить фото</p>}
      </label>
      <input
        id="file-upload"
        type="file"
        accept=".png, .jpg, .jpeg"
        {...other}
        onChange={(e) => {
          setImg(e.target.files[0]);
        }}
      />
    </>
  );
};

export default Input;
